func:
// Parse geolocation values from API response
msg.latitude = msg.payload.lat;
msg.longitude = msg.payload.lon;
return msg;

// Parse air quality values from API response
var airQuality = msg.payload["list"]["0"].main.aqi;
msg.air = airQuality == 1 ? "Good" 
            : airQuality == 2 ? "Fair"
            : airQaulity == 3 ? "Moderate"
            : airQuality == 4 ? "Poor"
            : "Very Poor";

// Build the array to be displayed on bar graph
msg.payload = [{
    "series": [ "CO", "NO", "O3", "SO2", "NH3"],
    "data": [
        msg.payload["list"]["0"].components.co,
        msg.payload["list"]["0"].components.no,
        msg.payload["list"]["0"].components.o3,
        msg.payload["list"]["0"].components.so2,
        msg.payload["list"]["0"].components.nh3
    ],
    "labels": ["Carbon Monoxide", "Nitrogen Monoxide", "Ozone", "Sulphur Dioxide", "Ammonia"]
}]

// Build output command for outdoor air condition
msg.tempOutdoorCommand = "The current air condition in your area is " + msg.air;

// Get the highest gas concentration value and its name in the list
msg.highestGasValue = Math.max(...msg.payload[0].data);
msg.highestGasName = msg.payload[0].labels[msg.payload[0].data.indexOf(msg.highestGasValue)];

// Get the highest gas concentration value in the list
msg.lowestGasValue = Math.min(...msg.payload[0].data);
msg.lowestGasName = msg.payload[0].labels[msg.payload[0].data.indexOf(msg.lowestGasValue)];

// Build output command for gas concentration
var airConcentrationCommand = msg.tempOutdoorCommand + "The highest gas concentration in your area now is " + msg.highestGasName 
    + " with " + msg.highestGasValue + " micrograms per cubic meter air.";
msg.airConcentrationCommand = airConcentrationCommand;
msg.indoorAirCommand = airConcentrationCommand;
msg.outdoorAirCommand = airConcentrationCommand;

return msg;

// Read Air Condition value from sensor
var rawAirValue = global.get("indoorAir");

var turnOnAirPurifier = false;
var status = '';
var indoorAirCommand = 'Unknown';

// Build out response field messages
if (!(isNaN(rawAirValue))) {

    status = rawAirValue >= 700 ? "High Pollution" : rawAirValue > 300 && rawAirValue < 700 ? "Low Pollution" : "Fresh Air";
    indoorAirCommand = "Your indoor air condition is " + status;

    if (rawAirValue >= 700) {
        turnOnAirPurifier = true;
    }

    msg.payload = {
        status: status,
        value : rawAirValue,
        turnOnAirPurifier: turnOnAirPurifier,
        indoorAirCommand: indoorAirCommand
    };
}

return msg;

// Parse city, state and country code value from the API response
msg.payload.city = msg.payload.city;
msg.payload.state = msg.payload.region;
msg.payload.country = msg.payload.countryCode;
return msg;

// Weather condition 
msg.condition = msg.payload.weather[0].main;

// Actual Temperature
msg.outdoorTemperature = msg.payload.main.temp;

// Feel-like Temperature
msg.feelLikeTemperature = msg.payload.main.feels_like;

// Humidity
msg.outdoorHumidity = msg.payload.main.humidity;

// Output command to browser
msg.outdoorWeatherCommand = "The current temperature in your area is " + msg.outdoorTemperature 
        + " degree celsius but feel like " + msg.feelLikeTemperature + " degree celsius. The weather condition is " 
        + msg.condition + " and the humidity is " + msg.outdoorHumidity + " percents";

msg.indoorWeatherCommand = "The temperature inside your house is " + global.get("indoorTemperature") + " degree celsius, and the humidity inside your house is " + global.get("indoorHumidity") + " percents";

return msg;

// Retrieve values stored from global variables
var temperature = global.get("indoorTemperature");
var humidity = global.get("indoorHumidity");

// Check if the values read from sensors are valid numbers
if (!(isNaN(temperature)) ||
   !(isNaN(humidity))) {

    // Additional check for valid temperature and humidity values
    if (temperature < 100 && humidity <= 100) {
        // Build out response fields
        msg.payload = {
            indoorTemperature: temperature,
            indoorHumidity: humidity
        };  
    }
}

return msg;

// Raw value read from light sensor
var lightSensorValue = global.get("indoorLight");

// Calculate resistance of sensor
// Resource: https://wiki.seeedstudio.com/Grove-Light_Sensor/
var resistance = (1023 - lightSensorValue) * 10 / lightSensorValue;

// Threshold resistance
var threshold = 50;

var timeOfDay = "";

// Identify as night time when sensor exceeds threshold resistance
if (resistance > threshold) {
    timeOfDay = "Night Time";
} else {
    timeOfDay = "Day Time";
}

// Build out response fields
msg.payload = {
    resistance : resistance,
    rawValue : lightSensorValue,
    timeOfDay: timeOfDay
}

return msg;

// Turn on LED when sensor exceeds threshold resistance (Night time)
if (msg.payload.timeOfDay === "Night Time") {
    msg.payload = 255;
} else {
   msg.payload = 0; 
}
return msg;

// Value reads from sensor
var motion = global.get("motionDetection");
var command = "";
var sendReport = false;

// Detect if there is a motion less than 20cm range from the sensor
if (!(isNaN(motion)) && (motion < 20)) {
    command = "Someone is at the front door.";
    sendReport = true;
} else {
    command = "No one is at the front door.";
    sendReport = false;
}

// Build out response fields
msg.payload = {
    motion: motion,
    sendReport: sendReport,
    command: command
}

return msg;

var outfitCommand = null;
var weatherCondition = global.get("weatherCondition");
if (weatherCondition === 'Rain') {
    outfitCommand = "It will be rainning. Have you brought your umbrella?";
} else if (weatherCondition === 'Snow') {
    outfitCommand = "It will be snowing today. Please wear warm outfits and waterproof boots.";
} else if (weatherCondition === 'Clouds') {
    outfitCommand = "It is cloudy today. There is no forecast of rain or snow. Take care!";
} else {
    outfitCommand = "No outfit suggestion";
}

msg.payload.weather = {
    outfitCommand: outfitCommand
}

return msg;

// Broadcast to Amazon Echo device
if (msg.weather.outfitCommand) {
    msg.payload = msg.weather.outfitCommand;
    return msg;   
}

// Retrieve weather condition stored in global variable
msg.weather = global.get("weather");
return msg;

// Parse city value from the API response
msg.payload = msg.payload.city;
return msg;

msg.alexaOptions = [
    {"Indoor Temperature and Humidity": global.get("indoorWeatherCommand")},
    {"Indoor Air Condition": global.get("indoorAirCommand")},
    {"Outdoor Weather Condition": global.get("outdoorWeatherCommand")},
    {"Outdoor Air Condition": global.get("outdoorAirCommand")}
]

return msg;

// Parse city, state and country code value from the API response
msg.payload.city = msg.payload.city;
msg.payload.state = msg.payload.region;
msg.payload.country = msg.payload.countryCode;
return msg;

// Read Air Condition value from sensor
var rawAirValue = msg.payload;

// Build out response field messages
if (!(isNaN(rawAirValue))) {

    msg.payload = {
        value : rawAirValue
    };
}

return msg;

// Adds topic key and setting log message.

msg.topic = "motion";

msg.payload = msg.payload.command;


return msg;

// Adds topic key.

msg.topic = "indoor_hum";
msg.payload = msg.payload.humidity;

return msg;

// Adds topic key

msg.topic = "light";

return msg;

// Adds Topic key 
msg.topic = "outdoor_current_temp"

// City name
msg.cityName = msg.payload.name;

// Weather condition 
msg.condition = msg.payload.weather[0].main;

// Convert to celsius
msg.payload = Math.round(((msg.payload.main.temp - 32) * (5 / 9)) * 10) / 10;

return msg;

// Adds Topic key 
msg.topic = "outdoor_max_temp"

// City name
msg.cityName = msg.payload.name;

msg.payload = msg.payload.main.temp_max;

return msg;

// Adds Topic key 
msg.topic = "outdoor_min_temp"

// City name
msg.cityName = msg.payload.name;

msg.payload = msg.payload.main.temp_min;

return msg;

// Adds Topic key 

msg.topic = "outdoor_humidity"

msg.icon = msg.payload.weather[0].icon;
msg.iconUrl = "http://openweathermap.org/img/wn/" + msg.icon + "@2x.png";

// City name
msg.cityName = msg.payload.name;

msg.payload = msg.payload.main.humidity;


return msg;

// Adds Topic key 

msg.topic = "outdoor_cond"

msg.payload = msg.payload.weather[0].main;

return msg;

// Adds topic key.

msg.topic = "indoor_temp";
msg.payload = msg.payload.temperature;

return msg;

// Adds topic key.

msg.topic = "indoor_heat";
msg.payload = msg.payload.heatIndex;

return msg;

msg.topic = "air";

msg.payload = msg.payload.value;

return msg;

// Value reads from sensor
var motion = msg.payload;
var command = "";

// Detect if there is a motion less than 5cm range from the sensor
if (motion < 5) {
    command = "Someone is at the front door.";
} else {
    command = "No one is at the front door.";
}

// Build out response fields
msg.payload = {
    command: command
}

return msg;

// Parse city, state and country code value from the API response
msg.payload.city = msg.payload.city;
msg.payload.state = msg.payload.region;
msg.payload.country = msg.payload.countryCode;
return msg;

// Sets up the payload for database insertion.

msg.payload = {
    email: global.get("email"),
    password: "password"
};
return msg;
